(window.webpackJsonp=window.webpackJsonp||[]).push([[61],{411:function(t,n,a){"use strict";a.r(n);var e=a(12),s=Object(e.a)({},(function(){var t=this,n=t._self._c;return n("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[n("RouterBack"),t._v(" "),n("h2",{attrs:{id:"💡-激斗竞技场公告"}},[n("a",{staticClass:"header-anchor",attrs:{href:"#💡-激斗竞技场公告"}},[t._v("#")]),t._v(" 💡 激斗竞技场公告")]),t._v(" "),n("p",[n("img",{attrs:{src:"https://user-images.githubusercontent.com/78347270/116804663-7f3cd980-ab5b-11eb-856a-ce5a3b904b77.jpg",alt:"179459961_245768300671315_8153811986862364583_n"}})]),t._v(" "),n("div",{staticClass:"language- extra-class"},[n("pre",{pre:!0,attrs:{class:"language-text"}},[n("code",[t._v("此致親愛的法蘭城勇者，\n\n關於一直以來都有勇者提出的PVP耐久度問題，\n於遊戲正式上線後，全伺服器的競技場內進行的勇者對戰都將不會損耗任何的裝備耐久。\n主要考慮到我們後續會舉辦定期的PVP賽事，我們真誠希望勇者在練功聊天抓寵生產之餘，\n也能享受魔力寶貝一貫以來對戰的樂趣同時不造成勇者裝備損耗的心理壓力。\n\n與此同時，永恆初心基礎版本將作為魔力寶貝全新的1.0首部曲。\n而2.0資料片傳說之劍，將在希望之門的基礎上延伸關於魔劍與聖劍的分歧故事，\n會需要勇者進行任務時需要閃躲，命中等較高的裝備進行攻略。\n\n同時後續的部份任務將難以利用合擊進行攻略(或者是合擊所造成的損傷將大幅降低)。\n我們認為即使技能練至10級，包括乾坤一擲，諸刃，混亂攻擊，聖盾等等攻防技能，\n其實也不如大合擊的傷害來得大以及有效。而魔法師更因無法合擊傷害最大化，\n現在往往也不是王戰攻略的成員。\n\n我們更希望勇者是透過角色的技能練度、\n裝備的利用以及投入更多變化的組隊回合戰略方式，而不是純粹的清小怪，\n換斧頭普攻合擊的單一攻略方法。\n\n敬請勇者期待呢。\n")])])]),n("h4",{attrs:{id:"参考链接-facebook-魔力宝贝-永恒初心"}},[n("a",{staticClass:"header-anchor",attrs:{href:"#参考链接-facebook-魔力宝贝-永恒初心"}},[t._v("#")]),t._v(" 参考链接： "),n("a",{attrs:{href:"https://www.facebook.com/cg.originmood/posts/245769490671196",target:"_blank",rel:"noopener noreferrer"}},[t._v("Facebook 魔力宝贝：永恒初心"),n("OutboundLink")],1)])],1)}),[],!1,null,null,null);n.default=s.exports}}]);